$.fn.extend({createDiscussMode:function(data){var html,$this=$(this),publishBtn,ljc_discuss,textAreaContent,htmlName,reg1,htmlFullName;defaults={header:"评论区",textAreaRows:5,textAreaCols:45,placeholderText:"客官对小女子有何感想？",buttonText:"发表",ele:$this,disWidth:"1200px",sendDataUrl:"../php/ljc-discuss.php",getDataUrl:"../php/ljc-discuss-data.php",getAnswerDataUrl:"../php/answerData.php",reg:/\d{12}/,};data=$.extend({},defaults,data);console.log(data.getAnswerDataUrl);html="";ljc_discuss=$("<div id='ljc-discuss'></div>");html="<img src='../images/love2.png' class='love2'><h1>"+data.header+"</h1><hr><div id='ljc-discuss-main'>"+"<div id='ljc-publish'><textarea rows='"+data.textAreaRows+"' cols='"+data.textAreaCols+"' placeholder='"+data.placeholderText+"'></textarea>"+"<button class='ljc-publish-button'>"+data.buttonText+"</button></div>"+"</div>";ljc_discuss.html(html);data.ele.append(ljc_discuss);$("#ljc-discuss").css({width:data.disWidth});htmlFullName=window.location.pathname;reg1=data.reg;htmlName=htmlFullName.match(reg1)[0];console.log(htmlName);publishBtn=$(".ljc-publish-button");publishBtn.on("click",function(){var floors=$(".floors"),num;textAreaContent=$("textarea").val();if(floors.length){num=parseInt(floors.eq(floors.length-1).text())+1}else{num=1}createDiscussEle(textAreaContent,num);$.ajax({url:data.sendDataUrl,data:{textAreaContent:textAreaContent,htmlName:htmlName,num:num},type:"post",success:function(data){data==1?alert("发表成功"):alert("发表失败");location.reload()}})});(function(){var url=data.getAnswerDataUrl;$.ajax({url:data.getDataUrl,data:{htmlName:htmlName},type:"post",dataType:"json",success:function(data){console.log(data.getAnswerDataUrl);if(data=="0"){return}$.each(data,function(i,n){createDiscussEle(n[0],n[1]);var discuss_mode=$(".ljc-discussed-mode");if(!n[2]){return}else{for(var j=0;j<n[2].length;j++){createAnswerHtml(i,n[2][j])}}});var ansText=$(".ljc-discussed-answer");var ansArea=$(".answerText");ansText.each(function(index,ele){$(this).click(function(){ansArea.eq(index).toggle()})});var ansBtn=$(".answerBtn");ansBtn.each(function(index,ele){$(this).click(function(){var ansArea=$(".answerText").eq(index),content=ansArea.find("textarea").val(),discussID=ansArea.attr("data-id");if(content){console.log(url);$.ajax({url:url,type:"post",data:{content:content,discussID:discussID,htmlName:htmlName},success:function(data){if(data==1){alert("回复成功");createAnswerHtml(discussID-1,content);ansArea.css("display","none")}else{alert(data)}}})}else{alert("内容不能为空")}})})}})}());function createDiscussEle(textContent,num){var discussMode,discussedHtml;discussedHtml="<span class='floors'>"+num+"楼</span><span class='ljc-discussed-one'>"+textContent+"</span><span class='ljc-discussed-answer'><img src='../images/love.png'>"+"<span>回复</span></span>"+"<div class='answerText' data-id="+num+"><textarea rows='4' cols='40'></textarea><button class='answerBtn'>回复</button></div>";discussMode=$("<div class='ljc-discussed-mode'></div>");discussMode.html(discussedHtml);$this.append(discussMode);discussMode.css("width",data.disWidth)}function createAnswerHtml(index,content){var html=$("<div class='answerWords'>"),discuss_mode=$(".ljc-discussed-mode");html.html(content);discuss_mode.eq(index).append(html)}}});
